import Hero from "./Hero";
import { Link } from "react-router-dom";
import NoPoster from "../images/NoPoster.jpg"
import { useState } from "react";

//API=f06e799237d5a0a2f66c86b622aca3f8
//example link for movie searches = https://api.themoviedb.org/3/search/movie?api_key=f06e799237d5a0a2f66c86b622aca3f8&language=en-US&query=star%20wars&page=1&include_adult=false

const MovieCard = ({ movie }) => {
  const [isNoPoster, setIsNoPoster] = true //s useState(true)
  let thePosterUrl;
  if (movie.poster_path) {
    thePosterUrl = `https://image.tmdb.org/t/p/w500/${movie.poster_path}`;
    setIsNoPoster(false)
} else {
  setIsNoPoster(true)
  thePosterUrl = NoPoster
}

  const detailUrl = `/movies/${movie.id}`
  return (
    <div className="col-lg-3 col-md-3 col-2 my-4">

      <div class="card">
        <img src={thePosterUrl} class="card-img-top" alt={movie.original_title} />
        {isNoPoster && (
       <p className="txt-over-img" >{movie.original_title}</p>
      )}

        <div class="card-body">
          <h5 class="card-title">{movie.original_title}</h5>
          <Link to={detailUrl} class="btn btn-primary" >
            Show Details
          </Link>
        </div>
      </div>
      </div>

  );
};

const SearchView = ({ keyword, searchResults }) => {
  const title = `You are Searching for ${keyword}`;
  const resultsHtml =
    searchResults &&
    searchResults.map((obj, i) => {
      return <MovieCard movie={obj} key={i} />;
    });

  return (
    <>
      <Hero text={title} />
      {resultsHtml && (
          <div className="container">
            <div className="row">{resultsHtml}</div>
          </div>
      )}
    </>
  );
};
export default SearchView;
