import Hero from "./Hero"
import { useParams } from "react-router-dom"
import { useEffect, useState } from "react"

const MovieView = () => {
    const {id} = useParams()
    const [movieDetails,setMovieDetails] = useState({});
    const [isLoading,setIsLoading] = useState(true); //by default its loading means true

    useEffect(()=>{
    fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=f06e799237d5a0a2f66c86b622aca3f8&language=en-US`)
    .then(response => {response.json()})
    .then(data => {
        setMovieDetails(data)
        setIsLoading(false)
    })
    },[id])


    function renderMovieDetails(){
        if(isLoading)
            {return <Hero text="Loading..." />}

        if (movieDetails)
           { return <Hero text={movieDetails.original_title} />}
    }

    return renderMovieDetails()
    // console.log(id);


}

export default MovieView
