import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';




const NavBar2 = ({ searchText, setSearchText }) => {
  const navigate = useNavigate()

  const updateSearchText = (e) => {
    navigate('/search')
    setSearchText(e.target.value)
  }

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
  <div className="container-fluid">
    <Link className="navbar-brand" to="/">Movie Browser</Link>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <Link className="nav-link active" aria-current="page" to="/">Home</Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link" to="/about">About</Link>
        </li>


        <li className="nav-item">
          <Link className="nav-link">Coming Soon</Link>
        </li>
      </ul>
      <form className="d-flex">
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" value={searchText} onChange={updateSearchText} />
        <button onClick={(event => {
                            event.preventDefault();
                            try{
                                updateSearchText({searchText}.target.value)
                            }
                            catch (TypeError){
                                document.getElementById("searchArea").value = ""
                            }
                        })}>Search</button>

      </form>
    </div>
  </div>
</nav>
  );
};

export default NavBar2;
